ADDON = xbmcaddon.Addon(id='plugin.video.FilmOn')


def res():
    quality = ADDON.getSetting('res')
    if quality ==0:
        return ''
    if quality ==1:
        return '360p'
    if quality ==2:
        return '480p'

        
        
